const crypto = require('crypto');

function generateSecretKey(length) {
    const charset = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    let key = '';

    for (let i = 0; i < length; i++) {
        const randomIndex = Math.floor(crypto.randomBytes(4).readUInt32LE(0)/0xffffffff * charset.length);
        key += charset.charAt(randomIndex);
    }

    return key;
}

module.exports = {
    generateSecretKey
};

document.addEventListener("DOMContentLoaded", function() {
    const header = document.getElementById("myHeader");
    const headerTop = header.getBoundingClientRect().top;

    window.addEventListener("scroll", function() {
        const scrollPosition = window.scrollY;

        if (scrollPosition >= headerTop - 61) {
            header.classList.add("fixedd");
        } else {
            header.classList.remove("fixedd");
        }
    });
});

