window.addEventListener('scroll', function() {
    const clases = ['descripcion', 'modelos', 'planos', 'galeria', 'ubicacion', 'obra', 'contacto'];
    let contenedorVisible = null;

    clases.forEach(function(clase) {
        const container = document.getElementById(clase);
        const elements = document.querySelectorAll('.'+clase);
        const rect = container.getBoundingClientRect();
        const containerTop = rect.top;
        const containerBottom = rect.bottom;

        console.log(containerBottom);

        if (containerBottom >= 200 && !contenedorVisible) {
            console.log('Scroll dentro del contenedor:', clase);
            contenedorVisible = clase;

            elements.forEach(function(element) {
                element.classList.add('active');
            });
        } else {
            elements.forEach(function(element) {
                element.classList.remove('active');
            });
        }
    });
});
